    ::-webkit-scrollbar {
        width: 6px;
        border-radius: 10px;
    }

    ::-webkit-scrollbar-track {
        background: var(--mute-text-color);
    }

    ::-webkit-scrollbar-thumb {
        background: rgba(216, 216, 216, 0.9);
        border-radius: 10px;
    }

    /* PAGE STYLE */

    * {
        --mute-text-color: rgb(118, 121, 136);
        --info-color-darkest: rgb(38, 42, 65);
        --success-color-darker: rgb(90, 198, 197);
        --success-color-lighter: rgb(224, 245, 244);
        --info-color-darker: rgb(94, 159, 241);
        --info-color-lighter: rgb(227, 239, 253);
        --danger-color-darker: rgb(240, 142, 109);
        --danger-color-lighter: rgb(253, 238, 232);
        --secondary-color-darker: rgb(133, 145, 159);
        --secondary-color-lighter: rgb(236, 238, 240);
        --primary-color: rgb(54, 217, 216);
        --purple-color: rgb(137, 79, 198);
        --light-blue-color: rgb(213, 222, 234);
    }

    #project {
        background-color: var(--info-color-darkest) !important;
        overflow: hidden;
    }

    .dashboard {
        position: relative;
        width: 100%;
        display: grid;
        grid-template-columns: repeat(2, 50%);
        color: white;
        background: var(--info-color-darkest) !important;
        border-radius: 0;
        min-height: 100vh;
        overflow: hidden;
    }

    .project-detail-lateral {
        padding: 100px 162px;
        max-height: 100vh;
    }

    .project-content {
        position: fixed;
    }

    .welcome-banner h1 {
        margin: 0;
        font-size: 28px;
    }
    .welcome-banner p {
        margin: 24px 0 0 0;
        color: var(--secondary-color-lighter);
    }

    .project-status {
        background: rgba(255, 255, 255, 0.1);
        border-radius: 5px;
        padding: 7px;
        line-height: 20px;
    }

    .search-bar .btn {
        display: flex;
        justify-content: center;
        align-items: center;
        padding: 18px;
        color: white;
        background: rgba(255, 255, 255, 0.1);
        border-color: transparent;
        border-radius: 15px;
    }

    .section .section-title {
        margin-bottom: 30px;
    }
    .section .section-title .title {
        font-weight: bold;
    }
    .section .section-title .project-count {
        color: var(--mute-text-color);
    }

    #projects {
        margin-top: 40px;
    }

    .project-list {
        display: grid;
        gap: 64px 40px;
        grid-template-columns: repeat(3, 1fr);
        grid-template-rows: repeat(2, 1fr);
        padding: 0;
        margin: 0;
        list-style-type: none;
    }
    .project-list__item {
        display: flex;
        flex-direction: column;
        align-items: center;
    }
    .project-list__item .project-block {
        --badge-color: white;
        position: relative;
        display: flex;
        justify-content: center;
        align-items: center;
        width: 96px;
        height: 96px;
        background: var(--project-color, rgba(255, 255, 255, 0.1));
        border-radius: 15px;
    }
    .project-list__item .project-block[data-badge]::after {
        box-sizing: border-box;
        min-width: auto;
        width: 16px;
        height: 16px;
        border-radius: 50%;
        box-shadow: 0 0 0 2px var(--info-color-darkest);
    }
    .project-list__item .project-abbr {
        font-size: 18px;
        font-weight: bold;
    }
    .project-list__item .project-name {
        margin-top: 30px;
    }
    .project-list__item.active .project-block {
        box-sizing: border-box;
    }
    .project-list__item.active .project-block::before {
        position: absolute;
        content: "";
        top: -12px;
        left: -12px;
        right: -12px;
        bottom: -12px;
        box-sizing: border-box;
        background: var(--project-color);
        border-radius: 20px;
    }
    .project-list__item.active .project-block::after {
        position: absolute;
        content: attr(data-abbr);
        top: 0;
        left: 0;
        box-sizing: border-box;
        display: flex;
        justify-content: center;
        align-items: center;
        width: 100%;
        height: 100%;
        font-size: 18px;
        font-weight: bold;
        background: var(--project-color);
        box-shadow: 0 0 0 9px var(--info-color-darkest);
        border-radius: 10px;
    }

    .ripe-malinka {
        --project-color: linear-gradient(120deg, #f093fb 0%, #f5576c 100%);
    }

    .itmeo-branding {
        --project-color: linear-gradient(180deg, #2af598 0%, #009efd 100%);
    }

    .mixed-hopes {
        --project-color: linear-gradient(to top, #c471f5 0%, #fa71cd 100%);
    }

    .fly-high {
        --project-color: linear-gradient(to top, #48c6ef 0%, #6f86d6 100%);
    }

    .fruit-blend {
        --project-color: linear-gradient(to right, #f9d423 0%, #ff4e50 100%);
    }

    .home {
        position: absolute;
        top: -50px;
        left: -50px;
        display: flex;
        justify-content: center;
        align-items: center;
        box-sizing: border-box;
        width: 36px;
        height: 36px;
        background: rgba(255, 255, 255, 0.1);
        border-radius: 10px;
    }
    .home svg {
        fill: white;
    }

    .project-detail {
        margin: 8px;
        padding: 40px 40px;
        color: black;
        border-radius: 15px;
        overflow: auto;
        background: whitesmoke;
    }

    .project-url {
        color: var(--secondary-color-darkest)
    }

    .project-detail .section {
        margin-top: 72px;
    }
    .project-detail .section .section-title {
        display: flex;
        justify-content: space-between;
        align-items: center;
        margin-bottom: 12px;
        border-bottom: 1px solid var(--secondary-color-lighter);
    }
    .project-detail .section .section-title h3 {
        margin-top: 0;
        color: var(--info-color-darkest);
    }
    .project-detail .section .section-title .more svg {
        fill: var(--secondary-color-darker);
    }

    .project-header {
        display: flex;
        justify-content: space-between;
        backface-visibility: revert;
        padding: 0;
    }

    .project-intro h1 {
        margin-top: 0;
        color: var(--info-color-darkest);
    }
    .project-intro p {
        line-height: 1.5;
        color: var(--mute-text-color);
    }

    .avatar-list {
        display: flex;
        padding: 0 0 0 45px;
        margin: 0;
        list-style-type: none;
    }
    .avatar-list__item img {
        width: 36px;
        height: 36px;
        border: 2px solid white;
        border-radius: 50%;
    }
    .avatar-list__item:not(:last-child) {
        margin-right: -6px;
    }
    .avatar-list__item:nth-child(1) {
        z-index: 3;
    }
    .avatar-list__item:nth-child(2) {
        z-index: 2;
    }
    .avatar-list__item .add-user {
        position: relative;
        top: 6%;
        left: 56%;
        fill: var(--light-blue-color);
    }

    .bedges {
        background-color: rgba(38, 42, 65, .2);
        padding: 6px;
        border-radius: 5px;
    }

    .bedges img {
        margin-left: 10px;
    }

    .project-votes {
        position: relative;
        background-color: rgba(38, 42, 65, .2);
        width: auto;
        height: 19px;
        border-radius: 5px;
        display: flex;
        padding: 0;
        margin-top: 60px;
        margin-right: -170px;
        margin-left: 70px;
    }

    .project-icon {
        border-radius: 50px;
        border-color: var(--secondary-color-darkest);
        width: 80px;
        position: relative;
        margin-top: 100px;
        margin-right: -150px;
        margin-left: 70px;
        box-shadow: 0 1.4px 2.8px rgba(38, 42, 65, 0.041), 0 3.3px 6.7px rgba(38, 42, 65, 0.102), 0 6.3px 12.5px rgba(38, 42, 65, 0.15), 0 11.2px 22.3px rgba(38, 42, 65, 0.198), 0 20.9px 41.8px rgba(38, 42, 65, 0.259), 0 50px 100px rgba(38, 42, 65, 0.4);
    }

    #today {
        margin-top: 20px;
    }

    .task-list {
        padding: 0;
        margin: 0;
        list-style-type: none;
    }
    .task-list__item {
        display: flex;
        justify-content: space-between;
        align-items: center;
        white-space: nowrap;
    }
    .task-list__item .form-check-input {
        width: 24px;
        height: 24px;
        border-radius: 50%;
    }
    .task-list__item .form-check-input ~ .form-check-label {
        margin-left: 18px;
    }
    .task-list__item .form-check-input::before {
        top: 1px;
        width: 24px;
        height: 24px;
        display: flex;
        justify-content: center;
        align-items: center;
        line-height: 1;
    }
    .task-list__item .tag {
        box-sizing: border-box;
        width: 92px;
        padding: 6px 18px;
        border-radius: 10px;
    }

    #upcoming {
        margin-top: 42px;
    }

    .add-task {
        position: absolute;
        bottom: 26px;
        right: 26px;
        display: flex;
        justify-content: center;
        align-items: center;
        box-sizing: border-box;
        width: 58px;
        height: 58px;
        padding: 21px;
        line-height: 1;
